INSERT INTO `wp_postmeta` VALUES (6395, 407, 'short_description', '<span style=\"font-weight: 400;\">Để cho ra đời dòng sản phẩm dầu nhớt Yamalube chính hãng, Yamaha đã dành thời gian nghiên cứu và phát triển dòng sản phẩm phù hợp nhất với đặc tính vận hành của động cơ của hãng. Do đó Yamalube sẽ “am hiểu” động cơ Yamaha so với các dòng nhớt phổ thông khác trên thị trường và nếu được sử dụng đúng định kỳ, người dùng xe Yamaha hoàn toàn yên tâm về khả năng bảo vệ và tối ưu hóa động cơ của Yamalube.</span>');
INSERT INTO `wp_postmeta` VALUES (6396, 407, '_short_description', 'field_6156e12ffef0b');
INSERT INTO `wp_postmeta` VALUES (6397, 407, 'content', '&nbsp;\r\n\r\n<img class=\"alignnone size-full\" src=\"https://global.yamaha-motor.com/business/mc/customer-support/vn/yamalube/img/pict_005.png\" width=\"776\" height=\"738\" />\r\n\r\n<span style=\"font-weight: 400;\">Các dòng sản phẩm dầu nhớt Yamalube đa dạng đáp ứng yêu cầu của các loại động cơ và nhu cầu khác nhau. Dầu nhớt xe máy Yamalube được đảm bảo chất lượng nhờ khâu kiểm định nghiêm ngặt của hãng và phải vượt qua được các yêu cầu, tiêu chuẩn cao nhất về chất lượng của Yamaha. Khách hàng dễ dàng nhận biết đâu là dầu Yamalube chính hiệu nhờ tem chống giả: Đổi màu xanh sang hồng (giống công nghệ in tiền trên tờ đô la Mỹ) &amp; công nghệ in tem trực tiếp trên chai.</span>');
