INSERT INTO `wp_postmeta` VALUES (10001, 611, 'short_description', '<div dir=\"auto\"><em><strong>Francesco Bagnaia đã rút ngắn khoảng cách với Fabio Quartararo ở vị trí đầu bảng xếp hạng MotoGP 2021 sau khi vô địch San Marino MotoGP 2021 tại Misano Circuit, Chủ nhật (19/9). Chặng 14 được đánh giá là một chặng đua khốc liệt cho đến vòng đua cuối cùng khi tay đua Bagnaia lên quyết tâm cao độ để thu hẹp khoảng cách với tay đua Yamaha.</strong></em></div>');
INSERT INTO `wp_postmeta` VALUES (10002, 611, '_short_description', 'field_611277f24c36b');
INSERT INTO `wp_postmeta` VALUES (10003, 611, 'content', '<div dir=\"auto\">Quartararo đang đứng đầu bảng xếp hạng MotoGP 2021 với 234 điểm. Tay đua của Yamaha hơn Bagnaia 48 điểm, ở vị trí thứ hai với 186 điểm.</div>\r\n<div dir=\"auto\"><a href=\"https://yamaha-dev.demo-web.asia/wp/wp-content/uploads/2021/10/MEYMm__TIX7720.jpg.webp\"><img class=\"alignnone size-medium wp-image-613\" src=\"https://yamaha-dev.demo-web.asia/wp/wp-content/uploads/2021/10/MEYMm__TIX7720.jpg-300x200.webp\" alt=\"\" width=\"300\" height=\"200\" /></a></div>\r\n<div dir=\"auto\">\r\n<div dir=\"auto\">Franco Morbidelli, trở lại sau cuộc phẫu thuật đầu gối để ra mắt tại Monster Yamaha với tư cách là người thay thế toàn thời gian cho Vinales, về đích thứ 18 – chỉ sau người đồng hương – đồng đội cũ của Petronas Yamaha, Valentino Rossi.</div>\r\n<div dir=\"auto\"><a href=\"https://yamaha-dev.demo-web.asia/wp/wp-content/uploads/2021/10/241379917_4337379762981716_2257110053817652685_n.jpg.webp\"><img class=\"alignnone size-medium wp-image-614\" src=\"https://yamaha-dev.demo-web.asia/wp/wp-content/uploads/2021/10/241379917_4337379762981716_2257110053817652685_n.jpg-225x300.webp\" alt=\"\" width=\"225\" height=\"300\" /></a></div>\r\n<div dir=\"auto\">\r\n<div class=\"news-detail\">\r\n<div class=\"news-detail__content newsDetail\">\r\n<div class=\"post_content\">\r\n<div class=\"o9v6fnle cxmmr5t8 oygrvhab hcukyx3x c1et5uql ii04i59q\">\r\n<div dir=\"auto\">Tiếp quản chiếc chiến mã của của Morbidelli là Andrea Dovizioso, người về nhì 3 danh hiệu MotoGP, đã cán đích chặng 14 ở vị trí thứ 21.</div>\r\n</div>\r\n<div class=\"o9v6fnle cxmmr5t8 oygrvhab hcukyx3x c1et5uql ii04i59q\">\r\n<div dir=\"auto\">Cùng Yamaha Motor Việt Nam liên tục cập nhật những tin tức mới nhất về MotoGP 2021 nhé!</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>');
