INSERT INTO `wp_postmeta` VALUES (16164, 760, 'feature_benefits_0_description', 'Kế thừa thiết kế công thái học và khí động học từ MotoGP YZR-M1, R3 2020 là phiên bản tối ưu nhất từng được chế tạo. Hốc gió trung tâm theo kiểu M1 mang mang đến một diện mạo đặc trưng của dòng R series. Với động cơ mạnh mẽ và phân bổ trọng lượng lý tưởng 50/50, R3 sẽ khiến bạn hồi hộp trên mọi cung đường.');
