INSERT INTO `wp_postmeta` VALUES (16170, 760, 'feature_benefits_1_description', 'Diện mạo đặc trưng dòng R-series, gồm dàn yếm, kính gió và cặp đèn LED theo phong cách thể thao của R1, R3 là một trong những chiếc xe cuốn hút nhất phân khúc.');
