INSERT INTO `wp_postmeta` VALUES (16182, 760, 'feature_benefits_3_description', 'Khung sườn xe nhỏ gọn và được sản xuất từ thép chịu lực, mang lại sự cân bằng giữa độ cứng cáp và khả năng chịu lực. Trục cơ sở ngắn giúp R3 thêm năng động và linh hoạt, trong khi phân bổ trọng lượng 50/50 và chiều cao yên hợp lý giúp người lái dễ dàng điều khiển.');
