INSERT INTO `wp_postmeta` VALUES (17443, 764, 'feature_benefits_0_description', 'Động cơ 4 xi-lanh làm mát bằng chất lỏng 998cc mang lại sức mạnh bùng nổ ngay từ dải tua thấp. Với trình tự đánh lửa đặc trưng của khối động cơ crossplane, MT-10 sản sinh khối mãnh lực đều đặn và khả năng vận hành chuẩn xác.');
