INSERT INTO `wp_postmeta` VALUES (17449, 764, 'feature_benefits_1_description', 'Nhằm đem lại sự kiểm soát tối ưu cho người lái, MT-10 được trang bị bướm ga điện tử Yamaha (YCC-T) cũng như hệ thống kiểm soát hành trình có thể dễ dàng thiết lập khi vận hành trên 50 km/h. Xe còn 3 chế độ lái khác nhau, và ổ cắm DC 12V phù hợp nhiều loại trang bị. MT-10 được trang bị 3 chế độ kiểm soát lực kéo (TCS) đem lại cảm giác vận hành mượt mà trong mọi điều kiện đường. Hệ thống sang số nhanh cho phép chuyển số tức thì và bộ ly hợp chống trượt (A&S) giúp kiểm soát xe một cách hoàn hảo. ');
