INSERT INTO `wp_postmeta` VALUES (18230, 776, 'feature_benefits_0_title', 'Động cơ crossplane 890cc mới');
INSERT INTO `wp_postmeta` VALUES (18231, 776, '_feature_benefits_0_title', 'field_61110f194e8fe');
INSERT INTO `wp_postmeta` VALUES (18232, 776, 'feature_benefits_0_description', 'Động cơ 3 xy lanh DOHC dung tích 890cc hoàn toàn mới, mang đến sức mạnh bùng nổ ngay cả tại những dải tua thấp. Gần như tất cả bộ phận cấu thành động cơ đã được đổi mới, nhằm tối ưu trọng lượng và cải thiện hiệu suất với mức tiêu thụ mới đạt 4.8l/100km. ');
INSERT INTO `wp_postmeta` VALUES (18233, 776, '_feature_benefits_0_description', 'field_61110f244e8ff');
INSERT INTO `wp_postmeta` VALUES (18234, 776, 'feature_benefits_1_image', '770');
INSERT INTO `wp_postmeta` VALUES (18235, 776, '_feature_benefits_1_image', 'field_61110f054e8fd');
INSERT INTO `wp_postmeta` VALUES (18236, 776, 'feature_benefits_1_title', 'Thiết kế đột phá mới');
INSERT INTO `wp_postmeta` VALUES (18237, 776, '_feature_benefits_1_title', 'field_61110f194e8fe');
INSERT INTO `wp_postmeta` VALUES (18238, 776, 'feature_benefits_1_description', 'Phong trần và mạnh mẽ, thế hệ thứ 3 của MT-09 hứa hẹn sẽ tạo ra xu hướng mới. Với các đường nét khỏe khoắn, được tối giản các chi tiết thiết kế để bộc lộ sự linh hoạt và thanh thoát cùng lúc với sự hứng khởi của người lái. Ngoài ra, thiết kế khung kim cương còn tôn thêm vẻ đẹp của động cơ khi hoạt động. ');
INSERT INTO `wp_postmeta` VALUES (18239, 776, '_feature_benefits_1_description', 'field_61110f244e8ff');
INSERT INTO `wp_postmeta` VALUES (18240, 776, 'feature_benefits_2_image', '771');
INSERT INTO `wp_postmeta` VALUES (18241, 776, '_feature_benefits_2_image', 'field_61110f054e8fd');
INSERT INTO `wp_postmeta` VALUES (18242, 776, 'feature_benefits_2_title', 'Cảm biến IMU 6 trục và 4 hệ thống hỗ trợ người lái');
INSERT INTO `wp_postmeta` VALUES (18243, 776, '_feature_benefits_2_title', 'field_61110f194e8fe');
INSERT INTO `wp_postmeta` VALUES (18244, 776, 'feature_benefits_2_description', 'MT-09 kế thừa bộ cảm biến IMU 6 trục như trên mẫu R1 nhưng nhỏ hơn 50% và nhẹ hơn 40%, cho phép kiểm soát và phát huy tối đa sức mạnh của xe theo ý người sử dụng, đi kèm với 4 hệ thống hỗ trợ tối tân trong phân khúc gồm: Kiểm soát bám đường (Traction Control), Kiểm soát độ nghiêng (Slide Control), Kiểm soát bánh trước (Lift Control), và Kiểm soát lực phanh (Brake Control).');
INSERT INTO `wp_postmeta` VALUES (18245, 776, '_feature_benefits_2_description', 'field_61110f244e8ff');
INSERT INTO `wp_postmeta` VALUES (18246, 776, 'feature_benefits_3_image', '772');
INSERT INTO `wp_postmeta` VALUES (18247, 776, '_feature_benefits_3_image', 'field_61110f054e8fd');
INSERT INTO `wp_postmeta` VALUES (18248, 776, 'feature_benefits_3_title', 'Màn hình TFT 3.5in rực rỡ');
INSERT INTO `wp_postmeta` VALUES (18249, 776, '_feature_benefits_3_title', 'field_61110f194e8fe');
INSERT INTO `wp_postmeta` VALUES (18250, 776, 'feature_benefits_3_description', 'Màn hình TFT đa sắc hiển thị tốc độ theo hình thang, có khả năng thay đổi màu sắc theo tốc độ vòng tua, cùng với động hồ thời gian, nhiên liệu, quãng đường di chuyển, nhiệt độ máy, nhiệt độ không khí và chỉ báo sang số. Người dùng có thể dễ dàng tùy chỉnh thông tin hiển thị bằng các nút chuyển trên tay lái.');
INSERT INTO `wp_postmeta` VALUES (18251, 776, '_feature_benefits_3_description', 'field_61110f244e8ff');
INSERT INTO `wp_postmeta` VALUES (18252, 776, 'feature_benefits_4_image', '773');
INSERT INTO `wp_postmeta` VALUES (18253, 776, '_feature_benefits_4_image', 'field_61110f054e8fd');
INSERT INTO `wp_postmeta` VALUES (18254, 776, 'feature_benefits_4_title', 'Đèn pha LED nhỏ gọn, đa chức năng');
INSERT INTO `wp_postmeta` VALUES (18255, 776, '_feature_benefits_4_title', 'field_61110f194e8fe');
INSERT INTO `wp_postmeta` VALUES (18256, 776, 'feature_benefits_4_description', 'Đèn được tích hợp khả năng chiếu gần và chiếu xa, với độ lan tỏa lớn. Phần đèn viền được thiết kế với độ tương phản thấp và mềm mại cho mắt người đối diện. MT-09 còn kết hợp nhiều bóng LED, giúp đèn có thể phân bố cường độ ánh sáng trên diện rộng, tạo nên một diện mạo ấn tượng. ');
