INSERT INTO `wp_postmeta` VALUES (32814, 1373, 'short_description', '<span style=\"font-weight: 400;\">Gần khu vực bình xăng trên bất kỳ mẫu xe nào Yamaha sản xuất đều là một hình tròn được dập nổi rất bắt mắt. Đó chính là logo của Yamaha Motor nhưng ít người biết được ý nghĩa hình ảnh này.</span>');
INSERT INTO `wp_postmeta` VALUES (32815, 1373, '_short_description', 'field_611277f24c36b');
INSERT INTO `wp_postmeta` VALUES (32816, 1373, 'content', '<span style=\"font-weight: 400;\">Logo đầu tiên của Yamaha được ra mắt năm 1888, với hình ảnh chú chim phượng hoàng ngâm trong mình chiếc âm thoa. Đến nay, trải qua gần 130 năm lịch sử với nhiều lần thay đổi nhận diện thương hiệu, Yamaha luôn giữ gìn và tôn vinh giá trị cốt lõi được thể hiện ngay từ những ngày đầu.</span>\r\n\r\n<span style=\"font-weight: 400;\">Đọc thêm câu chuyện sứ mệnh phượng hoàng huyền thoại của chiếc logo này </span><b>tại đây</b><span style=\"font-weight: 400;\">.</span>\r\n\r\n<span style=\"font-weight: 400;\">Năm 1916, logo phượng hoàng được thay thế bằng hình ảnh 3 chiếc âm thoa đan xen vào nhau, biểu trưng cho sự cân bằng, hội tụ của 3 yếu tố trong âm nhạc: “giai điệu”, “nhịp điệu” và “hòa hợp”. Một số phiên bản logo khác ứng với từng sản phẩm nhạc cụ như piano, organ lần lượt ra đời, cũng như các phiên bản riêng cho ấn phẩm truyền thông.</span>\r\n\r\n<img class=\"alignnone size-full\" src=\"https://i2.wp.com/img.v3.news.zdn.vn/w660/Uploaded/wyhktpu/2016_08_30/Untitled.jpg\" width=\"660\" height=\"191\" />\r\n\r\n<span style=\"font-weight: 400;\">Khi đơn vị sản xuất động cơ chính thức tách khỏi tập đoàn và trở thành công Yamaha Motor năm 1955, hình ảnh ba âm thoa đan xen này tiếp tục được sử dụng nhưng có thêm một số cải tiến. Logo mới được thêm một vòng tròn bao phía ngoài, mô phỏng hình dáng chiếc bánh xe khi chuyển động, gợi nhắc tài tình về các sản phẩm xe máy và mô tô của công ty mới. </span>\r\n\r\n<a href=\"https://global.yamaha-motor.com/about/philosophy/img/logo_yamahamotor.png\"><span style=\"font-weight: 400;\"><img class=\"alignnone size-full\" src=\"https://global.yamaha-motor.com/about/philosophy/img/logo_yamahamotor.png\" width=\"696\" height=\"173\" /></span></a><span style=\"font-weight: 400;\"> </span>\r\n\r\n<span style=\"font-weight: 400;\">Kế thừa triết lý kinh doanh của nhà sáng lập Torakusu Yamaha, mỗi chiếc âm thoa vẫn đại diện cho ba đoàn thể lớn ở công ty là “công nghệ”, “sản xuất” và “bán hàng”. Ngoài ra, Logo mới vừa thể hiện cho sự đồng thuận, gắn kết chặt chẽ trong tập thể Yamaha, vừa biểu trưng khí chất mạnh mẽ của một hãng sản xuất xe máy: “Hãy cùng nhau chinh phục thị trường thế giới bằng sự phối hợn nhịp nhàng giữa công nghệ, sản xuất, và bán hàng.”</span>\r\n\r\n<span style=\"font-weight: 400;\">Ngày nay, ba âm thoa còn mang đại diện cho \"Khách hàng\", \"Xã hội\" và \"Cá nhân\", tượng trưng cho các nguyên tắc quản lý của Yamaha Motor là một công ty tạo ra giá trị vượt qua sự mong đợi của khách hàng, đồng thời thực hiện trách nhiệm đối với xã hội với tư cách là doanh nghiệp toàn cầu, và nuôi dưỡng văn hóa doanh nghiệp, nơi tất cả nhân viên đều cảm thấy tự hào.</span>');
