INSERT INTO `wp_postmeta` VALUES (32882, 1379, 'short_description', '<span style=\"font-weight: 400;\">Công ty TNHH Yamaha Motor Việt Nam được thành lập vào ngày 24 tháng năm 1998 với vốn điều lệ 37 triệu USD qua doanh giữa Việt Nam, Nhật Bản, và Malaysia. Công ty có trụ sở chính tại xã Trung Giã, huyện Sóc Sơn, Hà Nội.</span>');
INSERT INTO `wp_postmeta` VALUES (32883, 1379, '_short_description', 'field_611277f24c36b');
INSERT INTO `wp_postmeta` VALUES (32884, 1379, 'content', '<span style=\"font-weight: 400;\">Yamaha Motor Việt Nam (YMVN) chuyên sản xuất và lắp ráp xe máy nhãn hiệu Yamaha, linh kiện, phụ tùng xe máy và cung cấp dịch vụ bảo hành sửa chữa xe máy. Qua hơn 20 năm phát triển, hệ thống đại lý của Yamaha Motor Việt Nam đã đạt đến con số hơn 300 đại lý, cửa hàng trên khắp Việt Nam. Hệ thống tiêu chuẩn cũng như hình ảnh của đại lý, cửa hàng, phòng trưng bày cũng liên tục được nâng cấp. Hiện công ty đang vận hành hai nhà máy với diện tích hơn 100,000 m</span><span style=\"font-weight: 400;\">2</span><span style=\"font-weight: 400;\"> với đội ngũ hơn 6500 nhân viên.</span>\r\n\r\n<span style=\"font-weight: 400;\">Với phương châm “TRỞ THÀNH CÔNG TY TẠO RA KANDO”, Yamaha Motor Việt Nam không ngừng nỗ lực nhằm hiện thực hóa giấc mơ của mọi khách hàng với niềm đam mê và khát khao sáng tạo, luôn đi tiên phong mang lại những sản phẩm tuyệt hảo và giá trị vượt trội thỏa mãn sự mong đợi của khách hàng. Với khẩu hiệu “Khơi dậy đam mê”, tạo ra những giá trị và trải nghiệm vượt trội, làm phong phú cuộc sống cho mọi khách hàng, Yamaha Motor Việt Nam phấn đấu trở thành một thành viên tích cực của cộng đồng Việt Nam, nhanh chóng góp phần vào sự phát triển của ngành công nghiệp Việt Nam trong lĩnh vực sản xuất xe gắn máy. </span>');
