INSERT INTO `wp_postmeta` VALUES (32915, 1382, 'short_description', '<span style=\"font-weight: 400;\">Phượng hoàng chính là hình ảnh logo đầu tiên cùa Tập đoàn Yamaha. Ẩn ý đằng sau chiếc logo này vẫn được kế thừa và phát huy hơn 130 năm qua. </span>');
INSERT INTO `wp_postmeta` VALUES (32916, 1382, '_short_description', 'field_611277f24c36b');
INSERT INTO `wp_postmeta` VALUES (32917, 1382, 'content', '<span style=\"font-weight: 400;\">Năm 1887, ông Torakusu Yamaha thành lập Công ty Nippon Gakki chuyên sản xuất đàn piano - tiền thân của Tập đoàn Yahama ngày nay. </span>\r\n\r\n<span style=\"font-weight: 400;\">Một năm sau, ông quyết định sử dụng hình ảnh một chú chim phượng hoàng ngậm âm thoa làm biểu tượng của công ty. </span>\r\n\r\n<i><span style=\"font-weight: 400;\">Logo đầu tiên của tập đoàn Yamaha</span></i>\r\n\r\n<img class=\"alignnone size-full\" src=\"https://i.pinimg.com/originals/27/10/04/2710042f0dc32339f8e0763123bcfe8a.png\" width=\"320\" height=\"320\" />\r\n\r\n<span style=\"font-weight: 400;\">Trong đó, chiếc âm thoa thể hiện trí tuệ và nỗ lực chinh phục các thách thức của ông Torakusu trong quá trình chế tác thành công chiếc đàn organ đầu tiên của Nhật Bản. Chim phượng hoàng, hay được gọi trong là “Ho-oh”, là loài vật linh thiêng gợi lên sức sống mãnh liệt, đúng như sứ mệnh “mang đến cho đời những âm thanh và bản nhạc trong trẻo nhất” của Nippon Gakki trong mỗi sản phẩm. Mỗi chiếc đàn công ty sản xuất sẽ làm lay động cảm xúc và tâm hồn của người sử dụng.</span>\r\n\r\n<span style=\"font-weight: 400;\">Điều đặc biệt là Torakusu Yamaha chỉ gắn logo này lên những chiếc đàn có chất lượng cao nhất và độ hoàn thiện tỉ mỉ nhất, thể hiện mong muốn và đồng thời là cam kết về việc tạo ra sản phẩm đẳng cấp quốc tế của ông và cả công ty. </span>\r\n\r\n<span style=\"font-weight: 400;\">Qua các giai đoạn phát triển, mẫu logo này đã được thay thế và dần hoàn thiện thành logo ba âm thoa đan xen như hiện nay. Tuy nhiên, công ty Yamaha Motor vẫn luôn kế thừa tinh thần cam kết về các sản phẩm các chất lượng cao nhất, cũng như sứ mệnh làm lay động cảm xúc và tâm hồn của người sử dụng trong mỗi sản phẩm.</span>\r\n\r\n<span style=\"font-weight: 400;\">Tìm hiểu thêm về ý nghĩa logo của Yamaha ngày nay </span><b>tại đây</b><span style=\"font-weight: 400;\">.</span>');
